/*!

=========================================================
* Argon Dashboard PRO React - v1.2.1
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-pro-react
* Copyright 2021 Creative Tim (https://www.creative-tim.com)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from 'react';
// nodejs library that concatenates classes
import classnames from 'classnames';
// reactstrap components
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col,
} from 'reactstrap';
// core components
import SimpleHeader from 'components/Headers/SimpleHeader.js';

function Validation() {
  const [focused, setFocused] = React.useState(false);
  const [firstName, setfirstName] = React.useState('Mark');
  const [firstNameState, setfirstNameState] = React.useState(null);
  const [lastName, setlastName] = React.useState('Otto');
  const [lastNameState, setlastNameState] = React.useState(null);
  const [username, setusername] = React.useState('');
  const [usernameState, setusernameState] = React.useState(null);
  const [city, setcity] = React.useState('');
  const [cityState, setcityState] = React.useState(null);
  const [state, setstate] = React.useState('');
  const [stateState, setstateState] = React.useState(null);
  const [zip, setzip] = React.useState('');
  const [zipState, setzipState] = React.useState(null);
  const [checkbox, setcheckbox] = React.useState(false);
  const [checkboxState, setcheckboxState] = React.useState(null);
  const validateCustomStylesForm = () => {
    if (firstName === '') {
      setfirstNameState('invalid');
    } else {
      setfirstNameState('valid');
    }
    if (lastName === '') {
      setlastNameState('invalid');
    } else {
      setlastNameState('valid');
    }
    if (username === '') {
      setusernameState('invalid');
    } else {
      setusernameState('valid');
    }
    if (city === '') {
      setcityState('invalid');
    } else {
      setcityState('valid');
    }
    if (state === '') {
      setstateState('invalid');
    } else {
      setstateState('valid');
    }
    if (zip === '') {
      setzipState('invalid');
    } else {
      setzipState('valid');
    }
    if (checkbox === false) {
      setcheckboxState('invalid');
    } else {
      setcheckboxState('valid');
    }
  };
  return (
    <>
      <SimpleHeader name="Form validation" parentName="Forms" />
      <Container className="mt--6" fluid>
        <Row>
          <div className="col">
            <div className="card-wrapper">
              <Card>
                <CardHeader>
                  <h3 className="mb-0">Custom styles</h3>
                </CardHeader>
                <CardBody>
                  <Row>
                    <Col lg="8">
                      <p className="mb-0">
                        For custom form validation messages, you’ll need to add the novalidate boolean attribute to your <code>{`<form>`}</code>. This
                        disables the browser default feedback tooltips, but still provides access to the form validation APIs in JavaScript. <br />
                        <br />
                        When attempting to submit, you’ll see the <code>:invalid</code> and <code>:valid</code> styles applied to your form controls.
                      </p>
                    </Col>
                  </Row>
                  <hr />
                  <Form className="needs-validation" noValidate>
                    <div className="form-row">
                      <Col className="mb-3" md="4">
                        <label className="form-control-label" htmlFor="validationCustom01">
                          First name
                        </label>
                        <Input
                          defaultValue="Mark"
                          id="validationCustom01"
                          placeholder="First name"
                          type="text"
                          valid={firstNameState === 'valid'}
                          invalid={firstNameState === 'invalid'}
                          onChange={(e) => {
                            setfirstName(e.target.value);
                            if (e.target.value === '') {
                              setfirstNameState('invalid');
                            } else {
                              setfirstNameState('valid');
                            }
                          }}
                        />
                        <div className="valid-feedback">Looks good!</div>
                      </Col>
                      <Col className="mb-3" md="4">
                        <label className="form-control-label" htmlFor="validationCustom02">
                          Last name
                        </label>
                        <Input
                          defaultValue="Otto"
                          id="validationCustom02"
                          placeholder="Last name"
                          type="text"
                          valid={lastNameState === 'valid'}
                          invalid={lastNameState === 'invalid'}
                          onChange={(e) => {
                            setlastName(e.target.value);
                            if (e.target.value === '') {
                              setlastNameState('invalid');
                            } else {
                              setlastNameState('valid');
                            }
                          }}
                        />
                        <div className="valid-feedback">Looks good!</div>
                      </Col>
                      <Col className="mb-3" md="4">
                        <label className="form-control-label" htmlFor="validationCustomUsername">
                          Username
                        </label>
                        <Input
                          aria-describedby="inputGroupPrepend"
                          id="validationCustomUsername"
                          placeholder="Username"
                          type="text"
                          valid={usernameState === 'valid'}
                          invalid={usernameState === 'invalid'}
                          onChange={(e) => {
                            setusername(e.target.value);
                            if (e.target.value === '') {
                              setusernameState('invalid');
                            } else {
                              setusernameState('valid');
                            }
                          }}
                        />
                        <div className="invalid-feedback">Please choose a username.</div>
                      </Col>
                    </div>
                    <div className="form-row">
                      <Col className="mb-3" md="6">
                        <label className="form-control-label" htmlFor="validationCustom03">
                          City
                        </label>
                        <Input
                          id="validationCustom03"
                          placeholder="City"
